import React, { useState } from "react";
import ChatBox from "./Chatbox"; // This is your chatbot UI component
import { IoMdChatboxes } from "react-icons/io"; // Chat icon
import { MdClose } from "react-icons/md";        // Close icon

const ChatWidget = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div style={styles.widgetContainer}>
      {isOpen ? (
        <div style={styles.chatWindow}>
          <div style={styles.header}>
            <span>Chat With AI ðŸ¤–</span>
            <MdClose style={styles.icon} onClick={() => setIsOpen(false)} />
          </div>
          <ChatBox />
        </div>
      ) : (
        <button style={styles.fab} onClick={() => setIsOpen(true)}>
          <IoMdChatboxes size={28} />
        </button>
      )}
    </div>
  );
};

const styles = {
  widgetContainer: {
    position: "fixed",
    bottom: "20px",
    right: "20px",
    zIndex: 9999,
  },
  fab: {
    backgroundColor: "#6c2bd9",
    color: "#fff",
    border: "none",
    borderRadius: "50%",
    width: "60px",
    height: "60px",
    cursor: "pointer",
    boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
  },
  chatWindow: {
    width: "370px",
    height: "500px",
    backgroundColor: "#fff",
    borderRadius: "12px",
    boxShadow: "0 4px 20px rgba(0,0,0,0.2)",
    display: "flex",
    flexDirection: "column",
    overflow: "hidden",
  },
  header: {
    backgroundColor: "#6c2bd9",
    color: "#fff",
    padding: "10px 15px",
    fontWeight: "bold",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  icon: {
    cursor: "pointer",
  },
};

export default ChatWidget;
