import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:8000/api",
  withCredentials: false,
});

const ChatBox = () => {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const chatEndRef = useRef(null);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [chatHistory]);

  const sendMessage = async () => {
    if (!message.trim()) return;

    const newChat = { sender: "user", text: message };
    setChatHistory((prev) => [...prev, newChat]);

    try {
      const res = await api.post(
        "/chat",
        { message },
        { headers: { "Content-Type": "application/json" } }
      );
      const botReply = res.data.response;
      setChatHistory((prev) => [...prev, { sender: "bot", text: botReply }]);
    } catch (err) {
      console.error("Chatbot error:", err?.response?.data || err.message);
      setChatHistory((prev) => [
        ...prev,
        { sender: "bot", text: "Error connecting to chatbot." },
      ]);
    }

    setMessage("");
  };

  return (
    <div style={styles.chatContainer}>
      <h2 style={styles.header}>🤖 Bangla AI Assistant</h2>
      <div style={styles.chatBox}>
        {chatHistory.map((msg, i) => (
          <div
            key={i}
            style={{
              ...styles.message,
              alignSelf: msg.sender === "user" ? "flex-end" : "flex-start",
              backgroundColor: msg.sender === "user" ? "#b9fbc0" : "#e0e0e0",
            }}
          >
            <span style={styles.senderIcon}>
              {msg.sender === "user" ? "🧑‍🎓" : "🤖"}
            </span>{" "}
            {msg.text}
          </div>
        ))}
        <div ref={chatEndRef} />
      </div>
      <div style={styles.inputArea}>
        <input
          style={styles.input}
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="বার্তা টাইপ করুন..."
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button style={styles.button} onClick={sendMessage}>
          প্রেরণ
        </button>
      </div>
    </div>
  );
};

const styles = {
  chatContainer: {
    maxWidth: "600px",
    margin: "30px auto",
    padding: "20px",
    border: "1px solid #444",
    borderRadius: "12px",
    backgroundColor: "#121212",
    color: "#f0f0f0",
    fontFamily: "Segoe UI, sans-serif",
    boxShadow: "0 0 15px rgba(0, 0, 0, 0.4)",
  },
  header: {
    textAlign: "center",
    marginBottom: "15px",
    fontWeight: "bold",
    color: "#00ff94",
  },
  chatBox: {
    height: "350px",
    overflowY: "auto",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginBottom: "15px",
    padding: "10px",
    border: "1px solid #333",
    borderRadius: "10px",
    background: "#1f1f1f",
  },
  message: {
    padding: "10px 14px",
    borderRadius: "20px",
    maxWidth: "75%",
    wordWrap: "break-word",
    fontSize: "15px",
    lineHeight: "1.5",
    transition: "all 0.3s ease",
  },
  senderIcon: {
    fontWeight: "bold",
    marginRight: "8px",
  },
  inputArea: {
    display: "flex",
    gap: "10px",
    alignItems: "center",
  },
  input: {
    flex: 1,
    padding: "12px 16px",
    fontSize: "16px",
    borderRadius: "8px",
    border: "1px solid #555",
    backgroundColor: "#2a2a2a",
    color: "#fff",
  },
  button: {
    padding: "12px 20px",
    backgroundColor: "#00ff94",
    color: "#000",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontWeight: "bold",
    transition: "background 0.3s ease",
  },
};

export default ChatBox;
