import React, { useState } from "react";
import axios from "axios";

// ✅ Create an axios instance (optional but clean)
const api = axios.create({
  baseURL: "http://localhost:8000/api", // easier to switch to production later
  withCredentials: false,               // keep false unless cookies are used
});

const ChatBox = () => {
  const [message, setMessage] = useState("");
  const [chatHistory, setChatHistory] = useState([]);

  const sendMessage = async () => {
    if (!message.trim()) return;

    const newChat = { sender: "user", text: message };
    setChatHistory((prev) => [...prev, newChat]);

    try {
      const res = await api.post(
        "/chat",
        { message },
        {
          headers: {
            "Content-Type": "application/json",
            // mode: "cors" is only relevant in fetch; not needed in axios, so we can skip
          },
        }
      );

      const botReply = res.data.response;
      setChatHistory((prev) => [...prev, { sender: "bot", text: botReply }]);
    } catch (err) {
      console.error("Chatbot error:", err?.response?.data || err.message);
      setChatHistory((prev) => [
        ...prev,
        { sender: "bot", text: "Error connecting to chatbot." },
      ]);
    }

    setMessage("");
  };

  return (
    <div style={styles.chatContainer}>
      <h2>Chat with LLaMA</h2>
      <div style={styles.chatBox}>
        {chatHistory.map((msg, i) => (
          <div
            key={i}
            style={{
              ...styles.message,
              alignSelf: msg.sender === "user" ? "flex-end" : "flex-start",
              backgroundColor: msg.sender === "user" ? "#DCF8C6" : "#F1F0F0",
            }}
          >
            <b>{msg.sender}:</b> {msg.text}
          </div>
        ))}
      </div>
      <div style={styles.inputArea}>
        <input
          style={styles.input}
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button style={styles.button} onClick={sendMessage}>
          Send
        </button>
      </div>
    </div>
  );
};

const styles = {
  chatContainer: {
    maxWidth: "600px",
    margin: "30px auto",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    fontFamily: "Arial",
  },
  chatBox: {
    height: "300px",
    overflowY: "auto",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginBottom: "15px",
    padding: "10px",
    border: "1px solid #eee",
    borderRadius: "8px",
    background: "#fff",
  },
  message: {
    padding: "10px",
    borderRadius: "8px",
    maxWidth: "70%",
    wordWrap: "break-word",
  },
  inputArea: {
    display: "flex",
    gap: "10px",
  },
  input: {
    flex: 1,
    padding: "10px",
    fontSize: "16px",
  },
  button: {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default ChatBox;
